// Parse AlertManager webhook payload
const alerts = $input.item.json.alerts || [];
const status = $input.item.json.status;
const groupKey = $input.item.json.groupKey;

// Process each alert
const processedAlerts = alerts.map(alert => {
  const severity = alert.labels.severity || 'info';
  const alertname = alert.labels.alertname;
  const service = alert.labels.service || alert.labels.job || 'unknown';
  const instance = alert.labels.instance || 'unknown';

  // Extract annotations
  const summary = alert.annotations.summary || alertname;
  const description = alert.annotations.description || 'No description';
  const grafanaUrl = alert.annotations.grafana_url || 'https://grafana.jclee.me';

  // Format alert message
  const emoji = {
    'critical': 'ðŸš¨',
    'warning': 'âš ï¸',
    'info': 'â„¹ï¸'
  }[severity] || 'ðŸ“¢';

  const statusEmoji = {
    'firing': 'ðŸ”¥',
    'resolved': 'âœ…'
  }[alert.status] || 'â“';

  return {
    severity,
    alertname,
    service,
    instance,
    summary,
    description,
    grafanaUrl,
    status: alert.status,
    startsAt: alert.startsAt,
    endsAt: alert.endsAt,
    fingerprint: alert.fingerprint,

    // Formatted message for notifications
    message: `${statusEmoji} ${emoji} **${alertname}** (${severity})\n` +
             `**Service**: ${service} | ${instance}\n` +
             `**Summary**: ${summary}\n` +
             `**Description**: ${description}\n` +
             `**Dashboard**: ${grafanaUrl}\n` +
             `**Status**: ${alert.status}\n` +
             `**Started**: ${new Date(alert.startsAt).toLocaleString('ko-KR', {timeZone: 'Asia/Seoul'})}`,

    // For logging to Loki
    logEntry: {
      timestamp: new Date().toISOString(),
      level: severity === 'critical' ? 'error' : severity === 'warning' ? 'warn' : 'info',
      alertname,
      service,
      instance,
      status: alert.status,
      summary,
      description,
      fingerprint: alert.fingerprint
    }
  };
});

return processedAlerts.map(alert => ({ json: alert }));

// Log to Loki (Prometheus format)
const alert = $input.item.json;

// Format as Loki log line
const logLine = JSON.stringify({
  timestamp: new Date().toISOString(),
  level: alert.logEntry.level,
  alertname: alert.alertname,
  service: alert.service,
  instance: alert.instance,
  status: alert.status,
  severity: alert.severity,
  summary: alert.summary,
  description: alert.description,
  fingerprint: alert.fingerprint
});

return [{
  json: {
    streams: [
      {
        stream: {
          job: 'alertmanager',
          alertname: alert.alertname,
          severity: alert.severity,
          service: alert.service
        },
        values: [
          [String(Date.now() * 1000000), logLine]
        ]
      }
    ]
  }
}];

// Console log for debugging
console.log('=== CRITICAL ALERT ===');
console.log($json.message);
console.log('=====================');

return $input.all();

// Console log for debugging
console.log('=== WARNING ALERT ===');
console.log($json.message);
console.log('=====================');

return $input.all();
